import React, { useContext, useEffect, useState } from 'react';
import ArtContext from '../context/ArtContext';
import '../css/Home.css';

function Home() {
  const { galleryObj } = useContext(ArtContext);
  const [galleryonScreen, setGalleryonScreen] = useState([]);

  useEffect(() => {
    const galleryArr = [];
    galleryObj.forEach((item, index) => {
      if (index % 3 === 0) {
        galleryArr.push([]);
      }
      galleryArr[Math.floor(index / 3)] = [...galleryArr[Math.floor(index / 3)], item];
    });

    setGalleryonScreen(galleryArr);
  }, [setGalleryonScreen]);

  return (
    <main className="main-container">
      <h1>Home</h1>
      <div className="cards-container">
        {galleryonScreen.map((item) => console.log(item))}
      </div>
    </main>
  );
}

export default Home;
