import React, { useContext, useEffect/* , useState  */ } from 'react';
import ArtContext from '../context/ArtContext';
import '../css/Home.css';

function Home() {
  const { galleryObj } = useContext(ArtContext);
  /* const [galleryonScreen, setGalleryonScreen] = useState([]); */
  /*  console.log(galleryonScreen); */

  const galleryImage = () => {
    const algo = [];

    galleryObj.forEach((item, index) => {
      if (index % 3 === 0) {
        algo.push([]);
      }

      console.log(
        'testando',
        Math.floor(index / 3),
        'saida do arr',

        algo,
        'aqui',
        [...algo[Math.floor(index / 3)], item],
      );
      algo[Math.floor(index / 3)] = [...algo[Math.floor(index / 3)], item];
    });
    console.log(algo);
  };
  useEffect(() => {
    galleryImage();
  }, []);

  return (
    <main className="main-container">
      <h1>Home</h1>
      <div className="cards-container">
        {galleryObj.map(({ id, image }) => (
          <div className="cards" key={id}>
            <img className="card-image" src={image} alt="Imagem" />
          </div>
        ))}
      </div>
    </main>
  );
}

export default Home;
