export const saveUser = (name, email) => {
  localStorage.setItem('user', JSON.stringify({ name, email }));
};

export const saveArt = (id, image) => {
  localStorage.setItem(
    'arts',
    JSON.stringify([
      { id, image },
    ]),
  );
};
